import React, { Component , useContext, useState, useEffect} from 'react';
import UserContext from "../context/UserContext";
import {Button , Card} from "react-bootstrap";
import {useHistory} from "react-router-dom"
import ErrorNotice from "../Helpers/ErrorNotice"
import axios from "axios";
import { Document, Page,pdfjs } from 'react-pdf'; 
import '../Helpers/pdf.css';
import qs from "qs"

export function Home(){
  const { userData, setUserData } = useContext(UserContext);
  const [posts, setPosts] = useState(
        [
            {
                likes: undefined,
                comments: undefined,
                id: undefined,
                userId: undefined,
                body: undefined
            }
        ]
    );
  const [postid, setPostId] = useState();
  const history = useHistory(); 
  pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`; 
  const [numPages, setNumPages] = useState(null); 
  const [pageNumber, setPageNumber] = useState(1);   
  /*When document gets loaded successfully*/
  function onDocumentLoadSuccess({ numPages }) { 
    setNumPages(numPages); 
    setPageNumber(1); 
  } 

  function changePage(offset) { 
    setPageNumber(prevPageNumber => prevPageNumber + offset); 
  } 

  function previousPage() { 
    changePage(-1); 
  } 

  function nextPage() { 
    changePage(1); 
  }       
  const likes = async(id) =>{
    try {
            console.log(id)
            console.log(userData.user)
            let like = axios.post(
                "http://localhost:5000/api/posts/likes",
                {userid: userData.user ,postid :id},
            );
            window.location.reload()
        } catch(err){
          console.log(err);
        }
  }

  useEffect(() => {
        const getposts = async() =>{
        try {
            let token = localStorage.getItem("auth-token");

            let postRes = await axios.get(
                "http://localhost:5000/api/users/profile",
                {
                    headers: { "x-auth-token": token },
                }
            );
            setPosts(postRes.data)
            //console.log(postRes.data)
        } catch (err) {
            console.log(err)
        }
    };
    getposts()
    }, []);
  return (
    <div className = "LoggedIN">
     {userData.user?(
      <div className="home" >
        <Button variant="primary" href = "/newpost?fileuploaded=-1">New Post</Button>{' '}
        {posts.map((post, index) => (
          <>
          <div className="card home-card" style = {{ "background-color": "grey" , 
                                                      margin: "auto", width: "50%" ,padding: "10px"}}>
            <div className="pdfpost">
              <Document 
              file={post.filename}
              onLoadSuccess={onDocumentLoadSuccess} 
              > 
              <Page pageNumber={pageNumber} />
              </Document>
            </div>
            <div> 
          <div className="pagec"> 
              Page {pageNumber || (numPages ? 1 : '--')} of {numPages || '--'} 
          </div> 
          <div className="buttonc"> 
          <button 
          type="button"
          disabled={pageNumber <= 1} 
          onClick={previousPage} 
          className="Pre"> 
            Previous 
          </button> 
        <button 
        className="Nxt"
        type="button"
        disabled={pageNumber >= numPages} 
        onClick={nextPage} > 
          Next 
        </button> 
    </div> 
  </div>    <div className="card-content">
              <button type="button" className="btn btn-primary" 
                onClick={() => {likes(post._id);}}>
                <i className="far fa-heart" >
                  Like
                </i>
              </button>
              {post.likes}
              <br />
            <h6>{post.title}</h6>
            <p>{post.body}</p>
            
          </div>

      </div>
      <br /><br /><br />
      </>
      ))}
    </div>
     )
     :
     (
     	<>
     <Card>
    <Card.Img variant="top" src="https://www.azquotes.com/picture-quotes/quote-scientific-research-is-one-of-the-most-exciting-and-rewarding-of-occupations-frederick-sanger-100-92-43.jpg" style={ { height: 300} }/>
  </Card>
  <Card body style = {{"background-color": "white","color": "black"}}>We at ResearchIt believe in spreading research all over the world. Publishing , Criticizing , Improving is what makes the field of research so graceful</Card>
  <br /> <br />
  <h3 id = "headings"> Why Us??!</h3>
   <br /> <br /> <br />
   <div className="row row-cols-2 row-cols-md-2">
    <div className="col mb-4">
      <div className="card">
        <img src="data:image/jpeg;base64,"
             className="card-img-top"
             alt="..."/>
        <div className="card-body">
          <h5 className="card-title">Collaboration</h5>
          <p className="card-text">
            We let our users connect to other users thereby faciliatating collaboration with each other.
            <br /> Our users thereby connect to people from all over the world.
          </p>
        </div>
      </div>
    </div>
    <div className="col mb-4">
      <div className="card">
        <img src="https://www.cognibrain.com/wp-content/uploads/2018/02/How-to-Publish-your-Paper.jpg" alt="..."/>
        <div className="card-body">
          <h5 className="card-title">Publish your Paper</h5>
          <p className="card-text">
            Ever wanted to be noticed while publishing your paper? 
            <br />
            We allow a social forum to publish your research and look at your fellow researcher's work as well.

          </p>
        </div>
      </div>
    </div>
  </div>
   </>
     )}
    </div>
    )
}

